window.noUiSlider = {
  create: function(el, opts) {
    const div = document.createElement('div');
    div.className = 'noUi-slider';
    div.style.cssText = 'position:relative; height:10px; background:#f0f0f0; border-radius:4px; margin:15px 0;';

    const handle = document.createElement('div');
    handle.className = 'noUi-handle';
    handle.style.cssText = 'position:absolute; width:20px; height:20px; background:#fff; border:2px solid #007bff; border-radius:50%; top:-5px; cursor:pointer;';

    div.appendChild(handle);
    el.appendChild(div);

    const obj = {
      on: () => obj,
      set: (val) => {
        const percent = ((val - opts.range.min) / (opts.range.max - opts.range.min)) * 100;
        handle.style.left = percent + '%';
      },
      get: () => [opts.start || 0],
      destroy: () => div.remove()
    };

    obj.set(opts.start || opts.range.min);
    return obj;
  }
};
