class Pristine {
  constructor(form, config) {
    this.form = form;
    this.config = config || {};
    this.validators = [];
  }
  addValidator(field, validate, errorMessage) {
    this.validators.push({field, validate, errorMessage});
    return this;
  }
  validate() {
    let valid = true;
    this.validators.forEach(v => {
      if (!v.validate(v.field.value)) {
        valid = false;
        v.field.classList.add('pristine-error');
      } else {
        v.field.classList.remove('pristine-error');
      }
    });
    return valid;
  }
  destroy() {}
}
window.Pristine = Pristine;
